function showData(ext_id) {
    var ext = data.find(ext => ext.id == ext_id);
    if (ext) {
        $('#edit-modal-fire-ext #ext-id').val(ext.id);
        $('#edit-modal-fire-ext #size').val(ext.size);
        $('#edit-modal-fire-ext #type').val(ext.type);
        $('#edit-modal-fire-ext #place').val(ext.place);
        $('#edit-modal-fire-ext #floor').val(ext.floor);
        $('#edit-modal-fire-ext #expiration-date').val(ext.expiration_date);
        $('#edit-modal-fire-ext #location').val(ext.location);
        $('#edit-modal-fire-ext #observations').val(ext.observations);
    }
    $('#edit-modal-fire-ext').modal('show');
}

function deleteData(id, ext_id) {
    console.log(id)
    $('#delete-modal-' + id + ' #ext-id').val(ext_id);
    $('#delete-modal-' + id).modal('show');
}

